<?phpuse Zette\Diagnostics\TimerPanel;use Zette\Application\Route;use Zette\Config\ZetteBootstrap;/** * Uvodni inicializace aplikace * */require_once __DIR__.'/../library/Zette/Config/ZetteBootstrap.php';class Bootstrap extends ZetteBootstrap {	/**	 * Nastaveni helperu	 * @deprecated	 */	protected function _initHelpers() {		$view = $this->getResource('view');		$prefix = 'My_View_Helper';		$dir = APPLICATION_PATH . '/../library/My/View/Helper';		$view->addHelperPath($dir, $prefix);		TimerPanel::traceTime();	}	/**	 * Nastaveni prepisu URL	 *	 * @param array $options	 */	protected function _initRequest(array $options = array()) {		$router = $this->router;		// Statics		$router->addRoute('aboutUs', new Route('o-nas', array(				'module' => 'default',				'controller' => 'index',				'action' => 'about',			))		);		$router->addRoute('contact', new Route('kontakt', array(				'module' => 'default',				'controller' => 'index',				'action' => 'contact',			))		);		// Events		$router->addRoute('eventList', new Route('udalosti/:categoryId', array(				'module' => 'default',				'controller' => 'event',				'action' => 'list',				'categoryId' => '',			))		);		$router->addRoute('event', new Route('udalost/:id/:title', array(				'module' => 'default',				'controller' => 'event',				'action' => 'detail',			), array(				'id' => '\d+',			))		);                                // User accounts                $router->addRoute('userRegister', new Route('registrace', array(				'module' => 'default',				'controller' => 'user',				'action' => 'register',			))		);		$router->addRoute('userLogin', new Route('login', array(				'module' => 'default',				'controller' => 'user',				'action' => 'login',			))		);		$router->addRoute('userLogout', new Route('logout', array(				'module' => 'default',				'controller' => 'user',				'action' => 'logout',			))		);		$router->addRoute('userActivation', new Route('aktivace/:id/:password', array(				'module' => 'default',				'controller' => 'user',				'action' => 'activate',			), array(				'id' => '\d+',                                'password' => '[a-z0-9]{10}'			))		);                                                // Admin		$router->addRoute('adminIndex', new Route('administrace', array(				'module' => 'admin',				'controller' => 'index',				'action' => 'index',			))		);		$router->addRoute('newEvent', new Route('administrace/nova-udalost', array(				'module' => 'admin',				'controller' => 'index',				'action' => 'edit',			))		);		$router->addRoute('editEvent', new Route('administrace/editovat-udalost/:id', array(				'module' => 'admin',				'controller' => 'index',				'action' => 'edit',			), array(                            'id' => '\d+',                        ))		);		// Landing page @todo smazat pro real pages		$router->addRoute('landing', new Route('', array(				'module' => 'landing',				'controller' => 'index',				'action' => 'index',			))		);		TimerPanel::traceTime();	}}