<?phpuse Zette\UI\BaseController;use app\services\TitleLoader;/** * Controller pro uvodni a informaci stranky * */class IndexController extends BaseController {	/** @var TitleLoader */	protected $titleLoader;		/**	 * Uvodni konfigurace controlleru	 *	 */	public function init() {	}	public function setContext(TitleLoader $titleLoader) {		$this->titleLoader = $titleLoader;	}	/**	 * Uvodni stranka	 *	 */	public function indexAction() {		$this->view->title = $this->t($this->titleLoader->getTitle('Index:index'));	}	/**	 * O nÃ¡s	 */	public function aboutAction() {		$this->view->title = $this->t($this->titleLoader->getTitle('Index:about'));	}}