<?phpuse Zette\UI\BaseController;use app\services\TitleLoader;/** * Controller pro uvodni a informaci stranky * */class IndexController extends BaseController {	/** @var TitleLoader */	protected $titleLoader;		/**	 * Uvodni konfigurace controlleru	 *	 */	public function init() {	}	/**	 * Nastaví kontext contrloleru, Zde se pomocí Dependency Injection vloží do třídy instance služeb, které budou potřeba	 * Mezi služby se řadí také modely a DB modely	 * Je třeba nadefinovat modely v config.neon	 * @param app\services\TitleLoader $titleLoader	 */	public function setContext(TitleLoader $titleLoader) {		$this->titleLoader = $titleLoader;	}	/**	 * Uvodni stranka	 *	 */	public function indexAction() {		$this->view->title = $this->t($this->titleLoader->getTitle('Index:index'));	}	/**	 * O nás	 */	public function aboutAction() {		$this->view->title = $this->t($this->titleLoader->getTitle('Index:about'));	}}