<?phpuse Zette\UI\BaseController;use app\services\TitleLoader;/** * Controller pro uvodni a informaci stranky * */class IndexController extends BaseController {    /** @var TitleLoader */    protected $titleLoader;    /**     * Uvodni konfigurace controlleru     *     */    public function init() {            }    /**     * Nastaví kontext contrloleru, Zde se pomocí Dependency Injection vloží do třídy instance služeb, které budou potřeba     * Mezi služby se řadí také modely a DB modely     * Je třeba nadefinovat modely v config.neon     * @param app\services\TitleLoader $titleLoader     */    public function setContext(TitleLoader $titleLoader) {        $this->titleLoader = $titleLoader;    }    /**     * O nás     */    public function aboutAction() {        $this->template->title = $this->t($this->titleLoader->getTitle('Index:about'));    }    /**     * Kontakt     */    public function contactAction() {        $this->template->title = $this->t($this->titleLoader->getTitle('Index:contact'));    }    public function registerAction() {        $this->template->title = $this->t($this->titleLoader->getTitle('Index:register'));                // Formulář        $form = new RegisterForm();        $this->view->form = $form;                // Zpracování požadavku na registraci        if ($this->_request->isPost()) {            if ($form->isValid($this->_request->getPost())) { // Validace prošla, jde se na registraci                echo "OK";            }        }    }}