<?phpuse Zette\UI\BaseController;use app\services\TitleLoader;/** * Controller pro uvodni a informaci stranky * */class IndexController extends BaseController {    /** @var TitleLoader */    protected $titleLoader;    protected $userTable;    protected $authenticateTable;    /**     * Uvodni konfigurace controlleru     *     */    public function init() {            }    /**     * Nastaví kontext contrloleru, Zde se pomocí Dependency Injection vloží do třídy instance služeb, které budou potřeba     * Mezi služby se řadí také modely a DB modely     * Je třeba nadefinovat modely v config.neon     * @param app\services\TitleLoader $titleLoader     */    public function setContext(    TitleLoader $titleLoader, app\models\authentication\AuthenticateTable $authenticateTable, app\models\authentication\UserTable $userTable) {        $this->titleLoader = $titleLoader;        $this->authenticateTable = $authenticateTable;        $this->userTable = $userTable;    }    /**     * O nás     */    public function aboutAction() {        $this->template->title = $this->t($this->titleLoader->getTitle('Index:about'));    }    /**     * Kontakt     */    public function contactAction() {        $this->template->title = $this->t($this->titleLoader->getTitle('Index:contact'));    }}