<?php/** * Helper pro přidání http:// do adresy, pokus neexistuje * */class My_View_Helper_AddHttp extends Zend_View_Helper_Abstract {		/**	 * Pridani http:// do linku, který jí nemusí mít	 *	 * @param string $url Vstupni text do URL	 * @return string upravená URL	 */	public function addHttp($url) {            if (strlen($url) > 1) {                if (!preg_match("~^(?:f|ht)tps?://~i", $url)) {                    $url = "http://" . $url;                }            }            return $url;	}	}?>