<?php/** * This file is part of the Nette Framework (http://nette.org) * * Copyright (c) 2004 David Grudl (http://davidgrudl.com) * * For the full copyright and license information, please view * the file license.txt that was distributed with this source code. */namespace Zette\Database\Diagnostics;use Nette,		Nette\Database\Helpers,		Nette\Diagnostics\Debugger;use Zend_Db_Statement_Pdo;use Zend_Db_Profiler;/** * Debug panel for Nette\Database. * * @author     David Grudl */class ConnectionPanel extends Nette\Object implements Nette\Diagnostics\IBarPanel{	/** @var int maximum SQL length */	static public $maxLength = 1000;	/** @var int logged time */	private $totalTime = 0;	/** @var array */	private $queries = array();	/** @var string */	public $name;	/** @var bool|string explain queries? */	public $explain = TRUE;	/** @var bool */	public $disabled = FALSE;	/** @var array */	protected $statements = array();	/** @var Zend_Db_Profiler */	protected $profiler;	public function logQuery(Zend_Db_Statement_Pdo $result)	{		if ($this->disabled) {			return;		}		$source = NULL;		foreach (debug_backtrace(FALSE) as $row) {			if (isset($row['file']) && is_file($row['file']) && strpos($row['file'], NETTE_DIR . DIRECTORY_SEPARATOR) !== 0) {				if (isset($row['function']) && strpos($row['function'], 'call_user_func') === 0) continue;				if (isset($row['class']) && is_subclass_of($row['class'], '\\Nette\\Database\\Connection')) continue;				if (isset($row['class']) && (strstr($row['class'], 'Zette') === 0 || strstr($row['class'], 'Nette') === 0)) continue;				$source = array($row['file'], (int) $row['line']);				break;			}		}		/** @var \PDOStatement $stmt  */		$stmt = $result->getDriverStatement();		$id = $stmt->queryString;		$this->statements[$id] = array($result, $source);	}	public function setProfiler(\Zend_Db_Profiler $profiler) {		Debugger::$bar->addPanel($this);		$this->profiler = $profiler;	}	protected function queries() {		$queryProfiles = $this->profiler->getQueryProfiles();		$queries = array();		if ($queryProfiles)		/** @var \Zend_Db_Profiler_Query $queryProfile */		foreach ($queryProfiles as $queryProfile) {			$id = $queryProfile->getQuery();			$this->totalTime+= $queryProfile->getElapsedSecs();			if (isset($this->statements[$id])) {				/** @var Zend_Db_Statement_Pdo $stmt */				list($stmt, $source) = $this->statements[$id];				$count = $queryProfile->getQueryType();				$connection = $stmt->getAdapter();			} else {				$count = '-';				$connection = null;				$source = null;			}			$query = array($queryProfile->getQuery(), $queryProfile->getQueryParams(), $queryProfile->getElapsedSecs(), $count, $connection, $source);			$queries[] = $query;		}		return $queries;	}	public static function renderException($e)	{		if ($e instanceof \PDOException && isset($e->queryString)) {			return array(				'tab' => 'SQL',				'panel' => Helpers::dumpSql($e->queryString),			);		}	}	public function getTab()	{		return '<span title="Nette\\Database ' . htmlSpecialChars($this->name) . '">'				. '<img src="data:image/png;base64," />'				. count($this->queries()) . ' queries'				. ($this->totalTime ? ' / ' . sprintf('%0.1f', $this->totalTime * 1000) . 'ms' : '')				. '</span>';	}	public function getPanel()	{		$this->disabled = TRUE;		$s = '';		$h = 'htmlSpecialChars';		$queries = $this->queries();		foreach ($queries as $i => $query) {			/** @var \Zend_Db_Adapter_Pdo_Abstract $connection */			list($sql, $params, $time, $rows, $connection, $source) = $query;			$explain = NULL; // EXPLAIN is called here to work SELECT FOUND_ROWS()			if ($this->explain && preg_match('#\s*\(?\s*SELECT\s#iA', $sql) && $connection) {				try {					$cmd = is_string($this->explain) ? $this->explain : 'EXPLAIN';					$explain = $connection->query("$cmd $sql", $params)->fetchAll();				} catch (\PDOException $e) {}			}			$s .= '<tr><td>' . sprintf('%0.3f', $time * 1000);			if ($explain) {				static $counter;				$counter++;				$s .= "<br /><a href='#' class='nette-toggler' rel='#nette-DbConnectionPanel-row-$counter'>explain&nbsp;&#x25ba;</a>";			}			$s .= '</td><td class="nette-DbConnectionPanel-sql">' . Helpers::dumpSql(self::$maxLength ? Nette\Utils\Strings::truncate($sql, self::$maxLength) : $sql);			if ($explain) {				$s .= "<table id='nette-DbConnectionPanel-row-$counter' class='nette-collapsed'><tr>";				foreach ($explain[0] as $col => $foo) {					$s .= "<th>{$h($col)}</th>";				}				$s .= "</tr>";				foreach ($explain as $row) {					$s .= "<tr>";					foreach ($row as $col) {						$s .= "<td>{$h($col)}</td>";					}					$s .= "</tr>";				}				$s .= "</table>";			}			if ($source) {				$s .= Nette\Diagnostics\Helpers::editorLink($source[0], $source[1])->class('nette-DbConnectionPanel-source');			}			$s .= '</td><td>';			foreach ($params as $param) {				$s .= Debugger::dump($param, TRUE);			}			$s .= '</td><td>' . $rows . '</td></tr>';		}		return empty($queries) ? '' :				'<style> #nette-debug td.nette-DbConnectionPanel-sql { background: white !important }			#nette-debug .nette-DbConnectionPanel-source { color: #BBB !important } </style>			<h1>Queries: ' . count($queries) . ($this->totalTime ? ', time: ' . sprintf('%0.3f', $this->totalTime * 1000) . ' ms' : '') . '</h1>			<div class="nette-inner nette-DbConnectionPanel">			<table>				<tr><th>Time&nbsp;ms</th><th>SQL Statement</th><th>Params</th><th>Rows</th></tr>' . $s . '			</table>			</div>';	}}