<?phpnamespace Zette\UI;use Nette\DI\Container;use Zend_Controller_Action;use Zend_Controller_Request_Abstract;use Zend_Controller_Response_Abstract;/** * @author: Misak113 * @date-created: 2.10.12 */class BaseController extends Zend_Controller_Action {	/** @var Container */	protected $context;	public function __construct(Zend_Controller_Request_Abstract $request, Zend_Controller_Response_Abstract $response, array $invokeArgs = array()) {		parent::__construct($request, $response, $invokeArgs);		$this->context = $invokeArgs['bootstrap']->getContainer()->context;		$this->afterConstruct();	}	private function afterConstruct() {		if (method_exists($this, 'setContext')) {			$this->context->callMethod(array($this, 'setContext'));		}	}}