<?phpnamespace Zette\UI;use Nette\DI\Container;use Zend_Controller_Action;use Zend_Controller_Request_Abstract;use Zend_Controller_Response_Abstract;/** * @author: Misak113 * @date-created: 2.10.12 */class BaseController extends Zend_Controller_Action {	/** @var Container */	protected $context;	/** @var \Bootstrap */	protected $bootstrap;	/** @var \Nette\Templating\FileTemplate */	public $template;	/** @deprecated */	public $view;	public function __construct(Zend_Controller_Request_Abstract $request, Zend_Controller_Response_Abstract $response, array $invokeArgs = array()) {		parent::__construct($request, $response, $invokeArgs);		$this->bootstrap = $invokeArgs['bootstrap'];		$this->context = $this->bootstrap->getContainer()->context;		$this->template = $this->view;		$this->afterConstruct();	}	private function afterConstruct() {		if (method_exists($this, 'setContext')) {			$this->context->callMethod(array($this, 'setContext'));		}	}	/**	 * Přeloží text do aktuálně nastaveného jazyka	 * @param string $text	 * @param array $params	 * @return string	 */	protected function t($text, $params = array()) {		// @todo Translator		return vsprintf($text, $params);	}	protected function url() {		return call_user_func_array(array($this->view, 'url'), func_get_args());	}}